"use strict";var myApp=angular.module("searchModule",[]);myApp.controller("searchController",["$scope","$http",function($scope,$http){$scope.sendSearch=function(){$scope.searchValue=$scope.searchValue.replace(/[^a-z0-9+]/gi,"");$http.get("https://api.github.com/search/repositories?q="+$scope.searchValue).then(function successCallback(response){if(response.data.items.length!==0){$scope.searchData=response.data.items;$scope.searchProperties=Object.getOwnPropertyNames($scope.searchData[0])}else{console.log($scope.searchValue+" returns zero results")}},function errorCallback(response){console.log(response.statusText);console.log("error getting data from Github")})};$scope.enterPressed=function(key){if(key.which===13){$scope.sendSearch()}};$scope.outputResults=function(obj){return JSON.stringify(obj,null,4)};$scope.expandAccordion=function($event){var button;button=$event.target;button.classList.toggle("active");button.nextElementSibling.classList.toggle("show")}}]);